    public void 玩家诱惑目标(SkillObject skill, C_04_CalculateTargetTemptation task, MapObject 诱惑目标)
    {
        if (诱惑目标 == null || 诱惑目标.Dead || CurrentLevel + 2 < 诱惑目标.CurrentLevel || (!(诱惑目标 is MonsterObject) && !(诱惑目标 is PetObject)) || (诱惑目标 is PetObject && (skill.SkillLevel < 3 || this == (诱惑目标 as PetObject).Master)) || (task.检查铭文技能 && (!Skills.TryGetValue((ushort)(task.检查铭文编号 / 10), out var v) || v.InscriptionID != task.检查铭文编号 % 10)))
        {
            return;
        }
        bool flag = task.特定诱惑列表?.Contains(诱惑目标.Name) ?? false;
        bool flag2 = flag;
        float num = (flag ? task.特定诱惑概率 : 0f);
        float num2 = ((诱惑目标 is MonsterObject) ? (诱惑目标 as MonsterObject).BaseTemptationProbability : (诱惑目标 as PetObject).BaseTemptationProbability);
        if ((num2 += num) <= 0f)
        {
            return;
        }
        int num3 = ((task.基础诱惑数量?.Length > skill.SkillLevel) ? task.基础诱惑数量[skill.SkillLevel] : 0);
        int num4 = ((task.初始宠物等级?.Length > skill.SkillLevel) ? task.初始宠物等级[skill.SkillLevel] : 0);
        byte 额外诱惑数量 = task.额外诱惑数量;
        float 额外诱惑概率 = task.额外诱惑概率;
        int 额外诱惑时长 = task.额外诱惑时长;
        float num5 = 0f;
        int num6 = 0;
        int num7 = 0;
        foreach (BuffInfo value in Buffs.Values)
        {
            if ((value.Buff效果 & BuffEffectType.TemptationBoost) != 0)
            {
                num5 += value.Template.IncreasedTemptationChance;
                num6 += value.Template.TemptationDurationIncreased;
                num7 += value.Template.TemptationLevelIncreased;
            }
        }
        float num8 = (float)Math.Pow((CurrentLevel >= 诱惑目标.CurrentLevel) ? 1.2 : 0.8, Compute.Clamp(0, Math.Abs(诱惑目标.CurrentLevel - CurrentLevel), 2));
        if (!Compute.CalculateProbability(num2 * num8 * (1f + 额外诱惑概率 + num5)))
        {
            return;
        }
        if (诱惑目标.Buffs.ContainsKey(task.狂暴状态编号))
        {
            if (Pets.Count < num3 + 额外诱惑数量)
            {
                int num9 = Math.Min(num4 + num7, 7);
                int 宠物时长 = Config.怪物诱惑时长 + 额外诱惑时长 + num6;
                bool 绑定武器 = flag2 || num4 != 0 || 额外诱惑时长 != 0 || 额外诱惑概率 != 0f || Pets.Count >= num3;
                PetObject 宠物实例2 = ((诱惑目标 is MonsterObject 怪物实例2) ? new PetObject(this, 怪物实例2, (byte)Math.Max(怪物实例2.PetLevel, num9), 绑定武器, 宠物时长) : new PetObject(this, (PetObject)诱惑目标, (byte)num9, 绑定武器, 宠物时长));
                Enqueue(new SyncPetLevelPacket
                {
                    ObjectID = 宠物实例2.ObjectID,
                    PetLevel = 宠物实例2.PetLevel
                });
                Enqueue(new GameErrorMessagePacket
                {
                    ErrorCode = 9473,
                    Param1 = (int)PetMode
                });
                PetInfo.Add(宠物实例2.PInfo);
                Pets.Add(宠物实例2);
            }
        }
        else
        {
            诱惑目标.AddBuff(task.瘫痪状态编号, this);
        }
    }

    public void 玩家瞬间移动(SkillObject skill, C_07_CalculateTargetTeleportation task)
    {
        if (Compute.CalculateProbability(task.每级成功概率[skill.SkillLevel]) && !(CurrentMap.随机传送(CurrentPosition) == default(Point)))
        {
            Teleport(ResurrectionMap, AreaType.Random);
        }
        else
        {
            AddBuff(task.瞬移失败提示, this);
            AddBuff(task.失败添加Buff, this);
        }
        if (task.GainSkillExp)
        {
            GainSkillExperience(task.ExpSkillID);
        }
    }

    public void 扣除护盾时间(int 技能伤害)
    {
        foreach (BuffInfo item in Buffs.Values.ToList())
        {
            if (item.Buff分组 == 2535)
            {
                if ((item.剩余时间.V -= TimeSpan.FromSeconds(Math.Min(15f, (float)技能伤害 * 15f / 50f))) < TimeSpan.Zero)
                {
                    RemoveBuff(item.ID.V);
                    continue;
                }
                SendPacket(new 对象状态变动
                {
                    对象编号 = ObjectID,
                    Buff编号 = item.ID.V,
                    Buff索引 = item.ID.V,
                    当前层数 = item.当前层数.V,
                    剩余时间 = (int)item.剩余时间.V.TotalMilliseconds,
                    持续时间 = (int)item.持续时间.V.TotalMilliseconds
                });
            }
        }
    }

    public void DamageWeapon()
    {
        var dmg = SEngine.Random.Next(1, 6);
        if (Equipment.TryGetValue(0, out var v) && v.Dura.V > 0 && v.Dura.V > 0 && (本期特权 != 5 || !v.CanRepair) && (本期特权 != 4 || !Compute.CalculateProbability(0.5f)))
        {
            v.Dura.V = Math.Max(0, v.Dura.V - dmg);
            if (v.Dura.V <= 0 && BonusStats.Remove(v))
            {
                RefreshStats();
            }
            Enqueue(new DurabilityChangedPacket
            {
                Grid = v.Grid.V,
                Location = v.Location.V,
                Durability = v.Dura.V
            });
        }
    }

    public void DecreaseWeaponLuck()
    {
        if (Config.PK死亡幸运开关 == 1 && Equipment.TryGetValue(0, out var v) && v.Luck.V > -9 && Compute.CalculateProbability(0.1f))
        {
            v.Luck.V--;
            Enqueue(new SyncItemPacket
            {
                Description = v.ToArray()
            });
        }
    }

    public void DamageCombatEquipment(int dmg)
    {
        if (Equipment.TryGetValue(15, out var v))
        {
            v.Dura.V = Math.Max(0, v.Dura.V - dmg);
            if (v.Dura.V <= 0)
            {
                Enqueue(new DeleteItemPacket
                {
                    Grid = v.Grid.V,
                    Position = v.Location.V
                });
                玩家穿卸装备(装备穿戴部位.战具, v, null);
                Equipment.Remove(v.Location.V);
                v.Remove();
            }
            else
            {
                Enqueue(new DurabilityChangedPacket
                {
                    Grid = v.Grid.V,
                    Location = v.Location.V,
                    Durability = v.Dura.V
                });
            }
        }
    }

    public void DamageEquipment(int dmg)
    {
        dmg = Math.Min(10, dmg);
        foreach (EquipmentInfo value in Equipment.Values)
        {
            if (value.Dura.V > 0 && (本期特权 != 5 || !value.CanRepair) &&
