    public void 提取邮件附件(int 邮件编号)
    {
        if (Session.MailInfoTable.DataSheet.TryGetValue(邮件编号, out var value) && value is MailInfo 邮件数据)
        {
            if (!Mail.Contains(邮件数据))
            {
                Enqueue(new SocialErrorPacket
                {
                    ErrorCode = 6148
                });
                return;
            }
            if (邮件数据.Attachment.V == null)
            {
                Enqueue(new SocialErrorPacket
                {
                    ErrorCode = 6150
                });
                return;
            }
            if (RemainingInventorySpace <= 0)
            {
                Enqueue(new SocialErrorPacket
                {
                    ErrorCode = 1793
                });
                return;
            }
            int num = -1;
            byte b = 0;
            while (b < InventorySize)
            {
                if (Inventory.ContainsKey(b))
                {
                    b = (byte)(b + 1);
                    continue;
                }
                num = b;
                break;
            }
            if (num == -1)
            {
                Enqueue(new SocialErrorPacket
                {
                    ErrorCode = 1793
                });
                return;
            }
            Enqueue(new 成功提取附件
            {
                邮件编号 = 邮件数据.MailID
            });
            Inventory[(byte)num] = 邮件数据.Attachment.V;
            邮件数据.Attachment.V.Location.V = (byte)num;
            邮件数据.Attachment.V.Grid.V = 1;
            邮件数据.Attachment.V = null;
        }
        else
        {
            Enqueue(new SocialErrorPacket
            {
                ErrorCode = 6148
            });
        }
    } 
