    public int 判断点位类型(Point 坐标)
    {
        foreach (MapObject obj in CurrentMap[坐标].ToList())
        {
            if (obj.ObjectType == GameObjectType.Monster && obj.CanBeHit)
            {
                return 1;
            }
            if (obj.ObjectType == GameObjectType.Pet && obj.CanBeHit)
            {
                return 2;
            }
        }
        if (!CurrentMap.CanMove(坐标))
        {
            return 3;
        }
        return 0;
    }

